mapper.select:
/* 이용후기 목록 검색조회 */ SELECT cm_num ,id ,cm_title ,cm_view ,cm_date FROM community
${searchKey} LIKE CONCAT('%', #{searchValue},'%')
AND community_cate = '이용후기'
ORDER BY cm_num DESC;

