mapper.select:
/* 회사 목록 검색조회 */ SELECT company_number ,ci_name ,company_name ,company_addr ,company_cate ,company_birth FROM company_info
${searchKey} LIKE CONCAT('%', #{searchValue},'%')
ORDER BY company_number;

/* 사업장 목록 검색조회 */ SELECT wp_company_number ,wp_company_name ,wp_name ,wp_addr ,wp_cate ,wp_birth FROM work_place
${searchKey} LIKE CONCAT('%', #{searchValue},'%')
ORDER BY wp_company_number;

mapper.insert:
/* 부서 등록 */
INSERT INTO department
	(
	 de_code
	,wp_code
	,de_name
	,de_date
	)
VALUES 
	(#{departmentCode}, #{deWorkPlaceCode}, #{departmentName}, CURDATE());

mapper.select:
/* 부서 목록 검색조회 */ SELECT w.wp_code ,w.wp_company_name ,d.de_code ,d.de_name ,d.de_date FROM department AS d INNER JOIN work_place AS w ON d.wp_code = w.wp_code
${searchKey} LIKE CONCAT('%', #{searchValue},'%')
ORDER BY d.de_code;

/* 사원 목록 검색조회 */ SELECT e.e_code ,e.e_level_code ,e.de_code ,e.e_name ,e.e_pw ,e.e_date ,e.resign_date ,m.e_level FROM employee_info AS e INNER JOIN management_level AS m ON e.e_level_code = m.e_level_code
${searchKey} LIKE CONCAT('%', #{searchValue},'%')
ORDER BY e.e_code;
