update.foreach:
 
		    UPDATE 	goods_info 
		    SET		arrival= 'y' , arrival_date = curdate() 
		  
		    WHERE 	goods_info_code=#{item};

mapper.select:
SELECT g.goods_info_code as goodsInfoCode, /*상품정보 코드*/ g.id as id, /*아이디*/ g.g_name as gName, /*이름*/ g.arrival_date as arrivalDate, /*도착날짜*/ s.imp_date as goodsPrice, /*보관금액*/ g.buy_order_code as buyOrderCode/*주문서 번호*/
FROM goods_info AS g INNER JOIN storage_fee AS s ON g.goods_info_code = s.goods_info_code
WHERE s.total_imp_of_charge > 0
and ${searchKey} = #{searchValue}
;

SELECT g.goods_info_code as goodsInfoCode /*상품정보 코드*/ , g.g_name as gName/*상품명*/ , i.out_tracking_num as trackingNum/*출고운송장번호*/ , i.send_out as arrivalDate/*출고 날짜*/ , i.out_company as deliveryCompany/*출고 배송회사*/ FROM goods_info AS g INNER JOIN delivery_current_state AS d ON g.goods_info_code = d.goods_info_code INNER JOIN incoming AS i ON g.goods_info_code = i.goods_info_code where d.stat_info = "배송완료"
and ${searchKey} = #{searchValue}
;

SELECT g.goods_info_code as goodsInfoCode /*상품정보 코드*/ , g.g_name as gName/*상품명*/ , i.out_tracking_num as trackingNum/*출고운송장번호*/ , i.send_out as arrivalDate/*출고 날짜*/ , i.out_company as deliveryCompany/*출고 배송회사*/ FROM goods_info AS g INNER JOIN delivery_current_state AS d ON g.goods_info_code = d.goods_info_code INNER JOIN incoming AS i ON g.goods_info_code = i.goods_info_code where d.stat_info = "배송중"
and ${searchKey} = #{searchValue}
;

/*전체 입고 리스트*/ SELECT * FROM goods_info AS g INNER JOIN order_info AS o ON g.buy_order_code = o.buy_order_code
WHERE g.buy_order_code not IN (SELECT buy_order_code FROM goods_info WHERE arrival='n') AND g.buy_order_code IN (SELECT buy_order_code FROM goods_info WHERE arrival='y')
GROUP BY o.buy_order_code

/*일부 입고 리스트*/ SELECT * FROM goods_info AS g INNER JOIN order_info AS o ON g.buy_order_code = o.buy_order_code
WHERE g.buy_order_code IN (SELECT buy_order_code FROM goods_info WHERE arrival='n') AND g.buy_order_code IN (SELECT buy_order_code FROM goods_info WHERE arrival='y')
GROUP BY o.buy_order_code

select * from goods_info as g inner join order_info as o on g.buy_order_code = o.buy_order_code where g.buy_order_code = #{buyOrderCode} and o.step3 = 'y' and arrival = 'n'
and ${searchKey} = #{searchValue}
limit ${cri.skip}, ${cri.amount} ;

select * from order_info as o inner join goods_info as g on o.buy_order_code = g.buy_order_code where o.step3 = 'y' and g.buy_order_code IN (SELECT buy_order_code FROM goods_info WHERE arrival='n')
and ${searchKey} = #{searchValue}
group by g.buy_order_code ;

mapper.insert:
 	/* 입고 등록 */
	INSERT INTO incoming
		(incoming_code, goods_info_code, e_code, weight, volume)
	VALUES
		(#{incomingCode}, #{goodsInfoCode}, #{eCode}, ${incomingWeight}, ${incomingVolume});

mapper.update:
/* 오류입고 상태 업데이트 */
UPDATE delivery_current_state
SET		
	stat_info='입고 완료'
WHERE
goods_info_code = #{goodsInfoCode};

mapper.insert:
/* 오류입고 */
INSERT INTO error_incoming
	(error_code, goods_info_code, error_content, error_amount, error_state, error_comp_date)
VALUES
	(#{errorCode}, #{goodsInfoCode}, #{errorContent}, ${errorAmount}, #{errorState}, CURDATE());

mapper.update:
/* 오류입고 상태 업데이트 */
UPDATE delivery_current_state
SET		
	stat_info='오류입고'
WHERE
goods_info_code = #{goodsInfoCode};

/* 오류입고 물품 처리 상태 업데이트 */
UPDATE error_incoming
SET
	error_state = #{errorState}
WHERE
	goods_info_code = #{goodsInfoCode};
