mapper.update:
/* 공지사항 조회수 카운트 */
UPDATE notice_center
SET
	notice_view = notice_view + 1
where
	notice_num = #{noticeNumCode};

mapper.select:
/* 공지사항 검색 */ SELECT notice_num ,e_code ,notice_title ,notice_content ,notice_img ,notice_date ,notice_view FROM notice_center
${sk} LIKE CONCAT('%',#{sv},'%')
order by notice_num

mapper.update:
/* 공지사항 수정 */
UPDATE notice_center
SET
	notice_num= #{noticeNumCode},
	notice_title=#{noticeTitle},
	notice_content=#{noticeContent},
	notice_img=#{noticeImg}
WHERE
	notice_num= #{noticeNumCode}
