mapper.select:
/* 1:1문의 검색 */ SELECT mtm_num_code ,e_code ,mtm_cate ,mtm_title ,mtm_content ,mtm_answer ,mtm_img ,mtm_date ,mtm_situation FROM question_center
${sk} LIKE CONCAT('%',#{sv},'%')
order by mtm_num_code

/* 1:1문의 검색 */ SELECT mtm_num_code ,e_code ,mtm_cate ,mtm_title ,mtm_content ,mtm_answer ,mtm_img ,mtm_date ,mtm_situation FROM question_center
${sk} LIKE CONCAT('%',#{sv},'%')
order by mtm_num_code

mapper.delete:
/* 1:1문의 삭제 */
DELETE
FROM
	question_center
WHERE
	mtm_num_code = #{mtmNumCode}

mapper.update:
/* 1:1문의 수정 */
UPDATE question_center
SET
	mtm_num_code = #{mtmNumCode},
	mtm_cate=#{mtmCate},
	mtm_title=#{mtmTitle},
	mtm_content=#{mtmContent},
	mtm_img=#{mtmImg}
WHERE
	mtm_num_code= #{mtmNumCode}

/* 관리자페이지에서 문의 클릭시 처리상태 처리중으로 변경 */
UPDATE question_center
SET
	mtm_situation=if(mtm_situation='답변 완료','답변 완료','답변중')
WHERE 
	mtm_num_code = #{mtmNumCode}
